const fs = require('fs');
const path = require('path');

// Create uploads directory
if (!fs.existsSync('uploads')) {
  fs.mkdirSync('uploads');
}

// Sample image data (base64 encoded 1x1 pixel images)
const sampleImages = {
  'sundarbans1_before.jpg': 'data:image/jpeg;base64,',
  'sundarbans1_after.jpg': 'data:image/jpeg;base64,',
  'kabini1_before.jpg': 'data:image/jpeg;base64,',
  'kabini1_after.jpg': 'data:image/jpeg;base64,'
};

// Create sample image files
Object.entries(sampleImages).forEach(([filename, base64Data]) => {
  const base64 = base64Data.split(',')[1];
  const buffer = Buffer.from(base64, 'base64');
  fs.writeFileSync(path.join('uploads', filename), buffer);
  console.log(`Created sample image: ${filename}`);
});

console.log('Sample images created successfully!');
